<script>
import { sentMessage } from '@/api';
import { ElMessage } from 'element-plus'

export default {
  name: 'CollectionForm',
  data() {
    return {
      isDisabled: false,
      form: {
        name: '',
        department1: '',
        department2: '',
        adjust: true,
        qq: '',
        phone: '',
        advantage: '',
      },
    };
  },
  methods: {
    onSubmit(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          sentMessage(this.form).then(
              response => {
                ElMessage('提交成功')
                console.log(response);
              }
          ).catch((error) => {
            ElMessage.error('提交失败')
            console.log(error);
          });
        } else {
          console.log('error submit!!');
          return false;
        }
      });

    },
  },
};
</script>

